import React , {useState} from 'react';
import List from './List';
import Search from './Search';

import './App.css';

const stuff = [{name: "Whyayu", surname: "Geh"}, {name: "Mike", surname: "Hunt"}, {name: "Dixie", surname: "Normis"}, {name: "Hugh", surname: "Janus"}, {name: "Moe", surname: "Lester"}];

export default function App() {
    const [ SearchText, setSearchText ] = useState('');

    const handleSearch = e => {
        var usertext = e.target.value;
        setSearchText(usertext.toLowerCase());
    }

    function Alphabetical(a, b) {
        const Alpha = a.name;
        const Blpha = b.name;
         console.log(Alpha > Blpha);
         return (Alpha > Blpha) ? 1 : -1;
      }

    const filteredSearch = stuff.sort(Alphabetical).filter(searchlist => {return(
        searchlist.name.toLowerCase().includes(SearchText) || searchlist.surname.toLowerCase().includes(SearchText)
        )});

    return (
        <>
        <div className="App">
            <Search SearchThis={handleSearch}/>
            <List ListOfStuff={filteredSearch}/>
        </div>

        </>
    )
}
