import React , {useState} from 'react'
import List from './List'
import Search from './Search'

const People = [{name: "Carl"}, {name: "Adam"}, {name: "Jeffery"}, {name: "Donny"}, {name: "Freddy"}];

export default function App() {
    const [searchText, setSearchText]= useState('');

    const handleSearchChange = e => {
        setSearchText(e.target.value.toLowerCase())
    };

    const filterSearches = People.sort().filter(list => {return(list.name.toLowerCase().includes(searchText))});

    return (
        <>
            <Search action={handleSearchChange}/>
            <List list={filterSearches}/>
        </>
    )
}
