import React , {useState} from 'react';
import List from './List';
import Search from './Search';


const stuff = [{name: "Carl", surname: "Liguma"}, {name: "Caydon", surname: "Stoffels"}, {name: "Marlin", surname: "Segers"}, {name: "Vincent", surname: "Dalla"}, {name: "Member", surname: "Company"}];



export default function App() {
    const [ SearchText, setSearchText ] = useState('');

    const handleSearch = e => {
        var usertext = e.target.value;
        setSearchText(usertext.toLowerCase());
    }

    function compare(a, b) {
        const bandA = a.name;
        const bandB = b.name;

        let comparison = (bandA > bandB) ? 1 : -1;
        // if (bandA > bandB) {
        //   comparison = 1;
        // } else if (bandA < bandB) {
        //   comparison = -1;
        // }


        return comparison;
      }

    const filteredSearch = stuff.sort(compare).filter(searchlist => {return(
        searchlist.name.toLowerCase().includes(SearchText) || searchlist.surname.toLowerCase().includes(SearchText)
        )});

    return (
        <div>
            <Search SearchThis={handleSearch}/>
            <List ListOfStuff={filteredSearch}/>
        </div>
    )
}
