import React, { useState } from 'react'
import List from './List';

export default function SearchBar({ listArray }) {
    const [ SearchText, setSearchText ] = useState('');

    const handleSearchInputChange = event => {
        setSearchText(event.target.value);
     }

    const filteredSearch = listArray.filter(list => {
        return(
           list.name.includes(SearchText)
        )
    }
    );

    return (
        <div>
            <input id="SearchBar" type="text" placeholder="Search" onChange={handleSearchInputChange}></input>
            <hr/>
            <List lister={filteredSearch}/>
        </div>
    )
}
