import React , {useState} from 'react'
import List from './List'
import Search from './Search'

const People = [{name: "Carl"}, {name: "Adam"}, {name: "Jeffery"}, {name: "Donny"}, {name: "Freddy"}];

export default function App() {
    const [ SearchText, setSearchText ] = useState('');

    const handleSearchChange = e => {
        var usertext = e.target.value;
        setSearchText(usertext.toLowerCase());
    }

    // function Alphabetical(a, b) {
    //     const Alpha = a.name;
    //     const Blpha = b.name;
    //      console.log(Alpha > Blpha);
    //      return (Alpha > Blpha) ? 1 : -1;
    //   }

    const filterSearches = People.filter(searchlist => {return(
        searchlist.name.toLowerCase().includes(SearchText) || searchlist.surname.toLowerCase().includes(SearchText)
        )});

    return (
        <>
            <Search action={handleSearchChange}/>
            <List list={filterSearches}/>
        </>
    )
}
