import React , {useState} from 'react';
import List from './List';
import Search from './Search';


const stuff = [{name: "Carl", surname: "Liguma"}, {name: "Caydon", surname: "Stoffels"}, {name: "Marlin", surname: "Segers"}, {name: "Vincent", surname: "Dalla"}];



export default function App() {
    const [ SearchText, setSearchText ] = useState('');

    const handleSearch = e => {
        var usertext = e.target.value;
        setSearchText(usertext[0].toUpperCase());
    }


    const filteredSearch = stuff.filter(searchlist => {return(
        searchlist.name.includes(SearchText) || searchlist.surname.includes(SearchText)
        )});

    return (
        <div>
            <Search SearchThis={handleSearch}/>
            <List ListOfStuff={filteredSearch}/>
        </div>
    )
}
